package com.edu.java1;

public class ejercicio12_boletin1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Numeros_100_1_Do_While(0);
	}

	
	
	public static void Numeros_100_1_Do_While(int numero) {
		int i=100;
		
		do {
			
			
		
				System.out.println(i);
				i--;
		}while (i>=numero);
	}
}
